def move_robot(direction, steps):
    if direction not in ['left', 'right', 'forward', 'backward']:
        return "Invalid direction"

    positions = {'x': 0, 'y': 0}
    if direction == 'left':
        positions['x'] -= steps
    elif direction == 'right':
        positions['x'] += steps
    elif direction == 'forward':
        positions['y'] += steps
    elif direction == 'backward':
        positions['y'] -= steps

    return f"Robot moved {direction} by {steps} steps. New position: ({positions['x']}, {positions['y']})"

